import PlantItem from "./PlantItem";
import React, { useState, useEffect } from 'react';
import axios from 'axios';


export default function ShopingList({category, addToPanier}){
    const [plants, setPlants] = useState([]);
  
    useEffect(() => {
      
      axios.get('http://localhost:4000/plantList') 
        .then((response) => {
          setPlants(response.data);
        })
        .catch((error) => {
          console.error('Erreur lors de la récupération des plantes :', error);
        });
    }, []);

    const filterPlantList = (category)=>(
        category!=null && category!="Select a category ..."?
        plants.filter(plant => plant.category.match(category)):
        plants
    )
    const filteredPlantList = filterPlantList(category);

    return(
        <div className="planlist-container">
                {
                    plants.map((plant) => (
                        <PlantItem
                            plant={plant}
                            addToPanier={addToPanier}/>
                    ))
                }
        </div>
    )
}
