// Cart.js
import React, { useState } from 'react';
import CheckoutForm from './CheckoutForm';

const Cart = ({ listPanier }) => {
  const total = listPanier.reduce((acc, item) => acc + item.plant.price * item.quantity, 0);

  const [isCheckout, setIsCheckout] = useState(false);

  const handleCheckout = () => {
    setIsCheckout(true);
  };

  const handleCheckoutSubmit = (checkoutData) => {
    // Envoyez les données du panier et du formulaire de validation au backend ici
    console.log('Données du panier :', listPanier);
    console.log('Données du formulaire de validation :', checkoutData);
    // Réinitialisez le panier après la validation
    setListPanier([]);
  };

  return (
    <aside className="cart">
      <h2>Panier</h2>
      <ul>
        {listPanier.map((plant) => (
          <li key={plant.plant.id}>
            {plant.quantity} {plant.plant.name} {plant.plant.price}$
          </li>
        ))}
      </ul>
      <h2>Total: {total}$</h2>
      {!isCheckout ? (
        <button onClick={handleCheckout}>Valider le Panier</button>
      ) : (
        <CheckoutForm onSubmit={handleCheckoutSubmit} />
      )}
    </aside>
  );
};

export default Cart;
