import { useState } from "react";
import ShopingList from "./ShopingList";
import Cart from "./Cart";

let quantity = 1;

export default function Main(){
    const [category, setCategory] = useState("");
    const [listPanier, setListPanier] = useState([]);
    const addToPanier = (plant)=>{
        const filteredPanier = listPanier.filter(
            (plant)=>plant.plant.name.match(plant.name)
            );
            console.log("filtered panier : "+filteredPanier);
            filteredPanier==null?
            setListPanier([
                ...listPanier,
                {plant, quantity: 1}
            ]):
            setListPanier([
                {plant, quantity: quantity++}
            ]);
        console.log("Panier : "+listPanier);
    }
    return(
        <body className="body-container">
            <Cart listPanier={listPanier}/>
            <div className="categories-shoplist">
                <Categories 
                    handleChangeCateogry={(category)=>setCategory(category)}/>
                <ShopingList
                    category={category}
                    addToPanier={(plant)=>addToPanier(plant)}/>
            </div>
        </body>
    )
}
