// Cart.js
import React, { useState } from "react";
import CheckoutForm from "./CheckoutForm";

export default function Cart({ listPanier }) {
  const [showForm, setShowForm] = useState(false);

  const handleFormSubmit = (formData) => {
    // Envoyez formData au backend (voir étape 3)

    // Vous pouvez également envoyer le panier au backend ici (listPanier)
    console.log("Informations de la personne :", formData);
    console.log("Contenu du panier :", listPanier);
  };

  return (
    <aside className="cart">
      <h2>Panier</h2>
      <ul>
        {listPanier.map((plant) => (
          <li key={plant.plant.id}>
            {plant.quantity} {plant.plant.name} {plant.plant.price}$
          </li>
        ))}
      </ul>
      <h2>Total</h2>
      <p>Calcul du total ici...</p>
      <button onClick={() => setShowForm(true)}>Valider le Panier</button>
      {showForm && <CheckoutForm onSubmit={handleFormSubmit} />}
    </aside>
  );
}
